DROP DATABASE IF EXISTS ims;
CREATE DATABASE IF NOT EXISTS ims;
CREATE TABLE ims.user(user_id INT AUTO_INCREMENT,first_name VARCHAR(50) NOT NULL,last_name VARCHAR(50) NOT NULL,username VARCHAR(16) NOT NULL,PRIMARY KEY (user_id));
CREATE TABLE ims.customers(customer_id INT AUTO_INCREMENT,first_name VARCHAR(50) NOT NULL,last_name VARCHAR(50) NOT NULL,address VARCHAR(50) NOT NULL,email VARCHAR(50) NOT NULL,postcode VARCHAR(10) NOT NULL,user_id INT,PRIMARY KEY (customer_id),FOREIGN KEY (user_id) REFERENCES ims.user (user_id) ON DELETE CASCADE);
CREATE TABLE ims.items(item_id INT AUTO_INCREMENT,name VARCHAR(50) NOT NULL,price DOUBLE(4,2) NOT NULL,genre VARCHAR(40),min_players INT,max_players INT,avg_play_time INT,user_id INT,PRIMARY KEY (item_id),FOREIGN KEY (user_id) REFERENCES ims.user (user_id) ON DELETE CASCADE);
CREATE TABLE ims.orders(order_id INT AUTO_INCREMENT,customer_id INT,total_price DOUBLE(4,2) NOT NULL,date_ordered DATE NOT NULL,user_id INT,PRIMARY KEY (order_id),FOREIGN KEY (customer_id) REFERENCES ims.customers (customer_id) ON DELETE CASCADE, FOREIGN KEY (user_id) REFERENCES ims.user (user_id) ON DELETE CASCADE);
CREATE TABLE ims.order_items(order_id INT,item_id INT,FOREIGN KEY (order_id) REFERENCES ims.orders (order_id) ON DELETE CASCADE,FOREIGN KEY (item_id) REFERENCES ims.items (item_id) ON DELETE CASCADE);
CREATE TABLE ims.user_password(user_id INT,password VARBINARY(128) NOT NULL,FOREIGN KEY (user_id) REFERENCES ims.user (user_id) ON DELETE CASCADE);
